const char *const APP_NAME = "layers and extensions";
const char *const ENGINE_NAME = "ratel";

static void _printInstanceProperties() {
    uint32_t layerCount;
    vkEnumerateInstanceLayerProperties(&layerCount, nullptr);
    std::vector<VkLayerProperties> _availableLayers(layerCount);
    vkEnumerateInstanceLayerProperties(&layerCount, _availableLayers.data());
    printer::layers(_availableLayers, "instance");

    uint32_t extensionCount = 0;
    vkEnumerateInstanceExtensionProperties(nullptr, &extensionCount, nullptr);
    std::vector<VkExtensionProperties> _extensions(extensionCount);
    vkEnumerateInstanceExtensionProperties(nullptr, &extensionCount, _extensions.data());
    printer::extensions(_extensions, "instance");
}

static void _printDeviceProperties(VkPhysicalDevice device) {
    uint32_t layerCount;
    vkEnumerateDeviceLayerProperties(device, &layerCount, nullptr);
    std::vector<VkLayerProperties> _availableLayers(layerCount);
    vkEnumerateDeviceLayerProperties(device, &layerCount, _availableLayers.data());
    printer::layers(_availableLayers, "device");

    uint32_t extensionCount;
    vkEnumerateDeviceExtensionProperties(device, nullptr, &extensionCount, nullptr);
    std::vector<VkExtensionProperties> availableExtensions(extensionCount);
    vkEnumerateDeviceExtensionProperties(device, nullptr, &extensionCount, availableExtensions.data());
    printer::extensions(availableExtensions, "device");
}

static VkResult createInstance(const char *appName, const char *engineName, utils::vulkanContext *context) {
    VkApplicationInfo appInfo{};
    appInfo.sType = VK_STRUCTURE_TYPE_APPLICATION_INFO;
    appInfo.pApplicationName = appName;
    appInfo.applicationVersion = VK_MAKE_VERSION(0, 0, 1);
    appInfo.pEngineName = engineName;
    appInfo.engineVersion = VK_MAKE_VERSION(0, 0, 1);
    appInfo.apiVersion = VK_API_VERSION_1_0;

    VkInstanceCreateInfo createInfo{};
    createInfo.sType = VK_STRUCTURE_TYPE_INSTANCE_CREATE_INFO;
    createInfo.pApplicationInfo = &appInfo;
    createInfo.enabledLayerCount = 0;
    createInfo.enabledExtensionCount = 0;

    return vkCreateInstance(&createInfo, nullptr, &context->ins);
}

static bool getPhysicalDevice(utils::vulkanContext *ctx) {
    uint32_t _deviceCount = 0;
    vkEnumeratePhysicalDevices(ctx->ins, &_deviceCount, nullptr);
    if (_deviceCount == 0) {
        fprintf(stderr, "failed to find GPUs with Vulkan support!\n");
        return false;
    }
    std::vector<VkPhysicalDevice> _vecDevice(_deviceCount);
    vkEnumeratePhysicalDevices(ctx->ins, &_deviceCount, _vecDevice.data());

    fprintf(stdout, "device count(%d)\n", _deviceCount);
    for (const auto &device : _vecDevice) {
        _printDeviceProperties(ctx->phyDev);
    }
    return true;
}

int main(int argc, char **argv) {
    _printInstanceProperties();

    utils::vulkanContext ctx;
    CHECK_VULKAN(createInstance(APP_NAME, ENGINE_NAME, &ctx), "failed to create instance!\n");
    CHECK_BOOL(getPhysicalDevice(&ctx), nullptr);

    _printDeviceProperties(ctx.phyDev);

    utils::destroyContext(ctx);
    exit(EXIT_SUCCESS);
}
