int main(int argc, char **argv) {
    GLFWwindow *_wnd{nullptr};
    CHECK_BOOL(utils::createWindow(APP_NAME, &_wnd), nullptr);

    utils::vulkanContext ctx;
    CHECK_VULKAN(createInstance(APP_NAME, ENGINE_NAME, &ctx), "failed to create instance!\n");
    CHECK_VULKAN(createSurface(_wnd, &ctx), "failed to create surface!\n");
    fprintf(stdout, "surface created!\n");

    while (!glfwWindowShouldClose(_wnd)) {
        glfwPollEvents();
    }

    utils::destroyContext(ctx);
    exit(EXIT_SUCCESS);
}
